--
-- Table structure for table `quizzes`
--

DROP TABLE IF EXISTS `quizzes`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `quizzes` (
  `quiz_id` int(11) NOT NULL AUTO_INCREMENT,
  `quiz_type` smallint(6) NOT NULL CHECK (`quiz_type` <= 15 and `quiz_type` >= 0),
  `quiz_title` varchar(100) NOT NULL,
  `quiz_lvl` smallint(6) NOT NULL CHECK (`quiz_lvl` <= 1 and `quiz_lvl` >= 0),
  `choice_A` varchar(100) DEFAULT NULL,
  `choice_B` varchar(100) DEFAULT NULL,
  `choice_C` varchar(100) DEFAULT NULL,
  `choice_D` varchar(100) DEFAULT NULL,
  `answers` varchar(4) NOT NULL,
  `is_single` smallint(6) NOT NULL CHECK (`is_single` <= 1 and `is_single` >= 0),
  `solution` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`quiz_id`)
) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=utf8mb4;
/*!40101 SET character_set_client = @saved_cs_client */;

