INSERT INTO `quizzes` VALUES (1,0,'有\"呕家圣药\"之称的药物是：',1,'柴胡','辛夷','升麻','生姜','D',1,'生姜辛散温通，能温胃散寒，和中降逆，其止呕功良，随证配伍可治疗多种呕吐，故素有\"呕家圣药\"之称。其它'),(2,0,'羌活的性味是：',1,'辛、甘，温','辛、苦，温','辛、涩，温','辛、咸，温','B',1,'本试题是测试考生是否掌握了中药的性味是如何确定的。羌活为常用的辛温解表药，辛散发表，味苦除湿，温能散'),(3,0,'中医学中成功运用辨证论治的第一部专著',0,'《黄帝内经》','《难经》','《神农本草经》','《伤寒杂病论》','D',1,''),(4,0,'下列著名医家中被称为\"寒凉派\"代表的是',0,'叶天士','张从正','刘完素','朱丹溪','C',1,''),(5,0,'我国第一部药物学专著是',0,'《本草纲目》','《黄帝内经》','《神农本草经》','《千金要方》','C',1,''),(6,0,'桂枝治疗风寒表虚证，宜配伍：',1,'细辛','麻黄','白芍','附子','C',1,'桂枝辛甘温煦，甘温通阳扶卫，其开腠发汗之力较麻黄温和，而善于宣阳气于卫分，畅营血于肌表，故有助卫实表'),(7,0,'我国第一部药物学专著是',0,'《神农本草经》','《新修本草》','《本草纲目》','《黄帝内经》','A',1,''),(8,0,'气的最基本特性是',0,'运动','变化','物质','无形','C',1,''),(9,0,'人与天地息息相应的联系,气所发挥的作用是',0,'聚散','升降','转化','中介','D',1,''),(10,12,'根据阴阳属性的可分性，五脏中属于阳中之阴的脏是：',0,'肝','脾','心','肺','C',1,''),(11,0,'无阳则阴无以，无阴则阳无以化＂说明阴阳的：',0,'交互感应','互根互用','对立制约','相互转化','B',1,''),(12,0,'阴阳的相对性表现在：',0,'阴阳中复有阴阳','阴消则阳长','阴阳双方是通过比较而分阴阳的','阳制约阴','AC',0,''),(13,0,'请判断五行中土对于火的关系。',0,'所不胜','为母','为子','所胜','B',1,''),(14,3,'以下哪些可以归属于五行中的木类',0,'南','春','青','肝','BCD',0,''),(15,0,'根据五行相克规律确定的治法有',0,'壮水制火','佐金平木','益火培土','抑木扶土','BDA',0,''),(16,0,'\"见肝之病,知肝传脾\",从五行的关系看,其所指内容是',0,'木乘土','木克土','木生土','土侮木','A',1,''),(17,0,'中医学认为五脏的主要生理特点是',0,'藏精气而不泻,虚实而交替','传化物而藏，满而不能实 ','藏精气而不泻,满而不能实 ','藏精气而不泻,实而不能满','C',1,''),(18,2,'肾藏精是指藏 ',0,'后天之精','水谷之精','先天之精和后天之精','脏腑之精','C',1,''),(19,2,'哪项与肝藏血有关',0,'视物清晰','皮毛润泽','肌肉丰满','头发乌黑光泽 ','A',1,''),(20,5,'下列各项中，既属于六腑，又属于奇恒之腑的是 ',0,'脉','胆','膀胱','三焦','B',1,''),(21,10,'有泌别清浊功能的是下列哪项 ',0,'胃的生理功能','肾的生理功能','小肠的生理功能','大肠的生理功能','C',1,''),(22,0,'《素问·灵兰秘典论》说：\"（）者，决渎之官，水道出焉。\"',0,'大肠','三焦','小肠','六腑','B',1,''),(23,11,'痰的生成与贮存与哪两脏有关 ',0,'胃','脾','肾','肺','BD',0,''),(24,6,'痰的生成与贮存与哪两脏有关 ',0,'胃','脾','肾','肺','BD',0,'');
